import * as Svg from 'react-native-svg';
import * as React from 'react';
import {Platform, StyleSheet, View, TextInput, Text, TouchableOpacity} from 'react-native';
import axios from "axios";
import {LinearGradient} from "expo-linear-gradient";
import Logo from "../../images/logo.svg";
import Txt from "../../images/first-img.svg";
export default class Navigation extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            lastName: '',
            firstName: '',
            dateOfBirth: '',
            email: '',
            town: '',
            password: '',
            isSubmit : false
        }
    }

    _getFirstName = (text) => {
        this.setState({ firstName: text });
    };

    _getLastName = (text) => {
        this.setState({ lastName: text });
    };

    _getDateOfBirth = (text) => {
        this.setState({ dateOfBirth: text });
    };

    _getEmail = (text) => {
        this.setState({ email: text });
    };

    _getTown = (text) => {
        this.setState({ town: text });
    };

    _getPassword = (text) => {
        this.setState({ password: text });
    };

    _addUser = (e) => {
        e.preventDefault();
        let formData = new FormData();
        formData.append('firstName', this.state.firstName);
        formData.append('lastName', this.state.lastName);
        formData.append('dateOfBirth', this.state.dateOfBirth);
        formData.append('email', this.state.email);
        formData.append('town', this.state.town);
        formData.append('password', this.state.password);
        axios.post("http://localhost:8888/api/", formData)
            .then(
                this.setState({isSubmit: true})
            )
            .catch(err => console.log(err));
    };

    returnForm = () => {
          return this.state.isSubmit === false ? (
              <View style={{alignItems: 'center', justifyContent: 'center', flex: 1}}>
                  <View style={styles.containerLogo}>
                      <Logo width={300} height={100} />
                  </View>
                  <View style={{marginTop: 30}}>
                      <Text style={{marginBottom: 30, fontFamily: 'Gilroy-Bold'}}> Bienvenue {this.state.firstName}  {this.state.lastName}, connectes-toi pour utiliser nos services !</Text>
                      <TouchableOpacity
                          style={styles.buttonShadowConnect}>
                          <LinearGradient style={styles.button} colors={['#FD867E', '#FD7EAC']} start={[0, 0.65]} end={[0.70, 1]}>
                              <Text style={styles.textButton}>Connexion</Text>
                          </LinearGradient>
                      </TouchableOpacity>
                  </View>
              </View>) : (<View>
              <View style={styles.header}><Text>Nous rejoindre</Text>
              </View>
              <View style={styles.containerInput}>
                  <View style={styles.secondInputContainerTwoInputs}>
                      <Text style={styles.titleInput}>Nom</Text>
                      <TextInput style={styles.textInput}
                                 autoCapitalize={"sentences"}
                                 placeholder={"Boirin"}
                                 textContentType={"familyName"}
                                 onChangeText={this._getLastName}/>
                  </View>
                  <View style={styles.secondInputContainerTwoInputs}>
                      <Text style={styles.titleInput}>Pr√©nom</Text>
                      <TextInput style={styles.textInput} autoCapitalize={"sentences"} placeholder={"Teddy"} textContentType={"familyName"} onChangeText={this._getFirstName}/>
                  </View>
                  <View style={styles.containerInput}>
                      <Text style={styles.titleInput}>Date de naissance</Text>
                      <TextInput style={styles.textInput} keyboardType={"numbers-and-punctuation"} placeholder={"17/09/1997"} onChangeText={this._getDateOfBirth} />
                  </View>
                  <View style={styles.containerInput}>
                      <Text style={styles.titleInput}>Adresse mail</Text>
                      <TextInput style={styles.textInput} autoCapitalize={"none"} keyboardType={"email-address"} placeholder={"mail@mail.fr"} textContentType={"emailAddress"} onChangeText={this._getEmail} />
                  </View>
                  <View style={styles.containerInput}>
                      <Text style={styles.titleInput}>Ville</Text>
                      <TextInput style={styles.textInput} autoCapitalize={"sentences"} placeholder={"Paris"} textContentType={"addressCity"} onChangeText={this._getTown} />
                  </View>
                  <View style={styles.containerInput}>
                      <Text style={styles.titleInput}>Mot de passe</Text>
                      <TextInput style={styles.textInput} autoCapitalize={"none"} secureTextEntry={"true"} textContentType={"password"} onChangeText={this._getPassword} />
                  </View>

              </View>

              <View style={{alignItems: 'center'}}>
                  <TouchableOpacity
                      style={styles.buttonShadowConnect} onPress={this._addUser}>
                      <LinearGradient style={styles.button} colors={['#FD867E', '#FD7EAC']} start={[0, 0.65]} end={[0.70, 1]} >
                          <Text style={styles.textButton}>Je m'inscris</Text>
                      </LinearGradient>
                  </TouchableOpacity>
              </View>
          </View>)
    }

    render() {

        return (
            <View style={styles.container}>
                {this.returnForm()}
            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        marginTop: 50,
        flex: 1,
    },

    header: {
        backgroundColor: 'white',
        marginBottom: 20,
        padding: 15,
        height: '6%',
        width: '100%',
        alignItems: 'center',
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 5,
        },
        shadowOpacity: 0.05,
        shadowRadius: 3.84,
        elevation: 5,
    },

    textInput: {
        width: '100%',
        borderColor: '#FD867E',
        borderWidth: .5,
        padding: 10,
        borderRadius: 5,
        marginBottom: 20,

    },

    secondInputContainerTwoInputs: {
        width: "50%",
        padding: 5,
        justifyContent:"space-between",
        flexDirection: 'row',
        flexWrap: 'wrap',

    },
    titleInput: {
        marginBottom: 10
    },
    containerInput: {
        flexWrap: 'wrap',
        width: "100%",
        flexDirection: 'row',
        padding: 5
    },
    button: {
        height: 45,
        width: 330,
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: 20,
        marginBottom: 25
    },
    buttonConnect: {
        backgroundColor: '#3A3A3A'
    },
    textButton: {
        backgroundColor: 'transparent',
        fontSize: 17,
        color: '#fff',
        textTransform: 'uppercase',
    },
});
